                        if (!getSetting('usingToolsDone')) {
                            showAlert("Using Tools","First, indicate what is wrong and then rate how bad it is (0-10) in order to be given an appropriate tool to try. Not every tool will be a good fit for you. Give thumbs up to those you like and they will be listed as a favorite. Give thumbs down to ones you don't like and you won't see them again. You can always access the whole list of tools, too.",
                                "Ok",null,
                                "Don't show again",function() { setSetting('usingToolsDone','true'); }
                            );
                        }

Content.onsave:
(function() {
    var msg = null;
    if (!getVariable('symptom')) {
        msg = 'Please select the symptom you experienced.';
    } else if (!getVariable('when')) {
        msg = 'Please enter the time when the episode occurred.';
    } else if ((getVariable('symptom')!='Trouble Sleeping') && !getVariable('severity') && (getVariable('severity') !== 0)) {
        msg = 'Please enter the severity of the episode.';
    } else if ((getVariable('symptom')=='Trouble Sleeping') && !getVariable('sleepDuration')) {
        msg = 'Please enter the total time you actually slept on the night of the episode.';
    } else if ((getVariable('symptom')=='Trouble Sleeping') && !getVariable('bedDuration')) {
        msg = 'Please enter the total time you spent in bed on the night of the episode.';
    } else if ((getVariable('symptom')=='Trouble Sleeping') && (getVariable('bedDuration') < getVariable('sleepDuration'))) {
        msg = 'Time asleep must be less than time in bed.';
    /*
    } else if (!getVariable('duration')) {
        msg = 'Please enter the duration of the episode.';
    } else if (!getVariable('experience')) {
        msg = 'Please enter a little bit about what you experienced during the episode.';
    } else if (!getVariable('consequences')) {
        msg = 'Please enter a little bit about the consequences of the episode.';
    } else if (!getVariable('triggers')) {
        msg = 'Please enter the triggers that may have caused this episode.';
    } else if (!getVariable('copingTechniques')) {
        msg = 'Please enter any coping techniques you used.';
    */
    }

    if (msg) {
        showAlert('Incomplete Journal Entry',msg);
        return true;
    }

    setVariable('displayName',getVariable('symptom'));
    return false;
})()
