Content.onload:
js:runDelayed(3000, function() {
                    var isFirstLaunch = !(getSetting('firstLaunch')=='true');
                    var unsafe = (getSetting('userSafetyState')=='unsafe');
                    var dontShowPlanReminder = getSetting('dontShowPlanReminder');
                    var incompletePlan = dae.startedSafetyPlan && !dae.finishedSafetyPlan;
                    var unstartedPlan = !dae.startedSafetyPlan && !dae.finishedSafetyPlan;
                    setSetting('firstLaunch','true');
                    if (unsafe && unstartedPlan && !dontShowPlanReminder) {
                        showAlert('Make A Safety Plan','You have not completed a safety plan. Would you like to do so now?',
                            'Later',null,
                            'Yes',function() { gotoContent('personalizeSafetyPlan'); },
                            "Don't show again",function() { setSetting('dontShowPlanReminder','true'); }
                        );
                    } else if (unsafe && incompletePlan && !dontShowPlanReminder) {
                        showAlert('Finish Your Safety Plan','You have not finished your safety plan. Would you like to do so now?',
                            'Later',null,
                            'Yes',function() { gotoContent('personalizeSafetyPlan'); },
                            "Don't show again",function() { setSetting('dontShowPlanReminder','true'); }
                        );
                    } else if (!isFirstLaunch) {
                        var contactCount = countRefs('contact');
                        var audioCount = countRefs('audio');
                        var imageCount = countRefs('image');
                        var dontShowPersonalizationReminder = getSetting('dontShowPersonalizationReminder');
                        if (!dontShowPersonalizationReminder) {
                            if (!contactCount && !audioCount && !imageCount) {
                                showAlert('No Personal Supports','Select some people from your life that can help if the going gets tough.',
                                    'Later',null,
                                    'Yes',function() { gotoContent('setupContacts'); },
                                    "Don't show again",function() { setSetting('dontShowPersonalizationReminder','true'); }
                                );
                            } else if (contactCount && !audioCount && !imageCount) {
                                showAlert('No Personalized Tools','Select some photos or songs that can be integrated into stress management tools.',
                                    'Later',null,
                                    'Yes',function() { gotoContent('setup'); },
                                    "Don't show again",function() { setSetting('dontShowPersonalizationReminder','true'); }
                                );
                            }
                        }
                    }
                });

